UPDATE `locales_npc_text` SET `Text0_0_loc6` = 'Soy un oficial de la ciudad, trabajo en el censo de Entrañas. Desde la liberación de la plaga, los renegados hemos querido aumentar la popularidad de la ciudad. Mediante la magia arcana y la disciplina mental, lo hemos conseguido.' WHERE `entry` = 3653;
