UPDATE `locales_creature` SET `name_loc6` = 'Druman Arbolsombrío', `subname_loc6` = 'Heraldo del Festival Lunar' WHERE `entry` = 22229;
UPDATE `locales_npc_text` SET `Text0_0_loc6` = 'La celebración del Festival de Invierno es un modo de recordar lo felices que vivimos durante esta estación del año tan fría. El corazón del Festival se encuentra en Orgrimmar.', `Text0_1_loc6` = 'La celebración del Festival de Invierno es un modo de recordar lo felices que vivimos durante esta estación del año tan fría. El corazón del Festival se encuentra en Orgrimmar.' WHERE `entry` = 9706;
UPDATE `locales_npc_text` SET `Text0_0_loc6` = 'Alrededor del reno encuentras un cartel con el símbolo de Pastos Bosquehumeante y con un nombre que dice: "- Metzen".$B$BMetzen te mira con cara de tristeza y con ojos llorosos. Parece como si este reno hubiese sido inmovilizado por una fuerza invisible.' WHERE `entry` = 8076;
UPDATE `locales_npc_text` SET `Text0_0_loc6` = 'Los ojos de Metzen se iluminan con un sorprendente destello que te impresiona al verlo. Choca sus astas contra tí y emite un gruñido mostrando su agradecimiento.$B$BAlrededor de ti, empieza a surgir una energía extraña. ¡Parece que algo fuese a ocurrir!' WHERE `entry` = 8077;

UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Hay una nota en el regalo que dice:$B$B"Para $N:$B$BEn agradecimiento por tus hazañas llevadas acabo en Azeroth durante todo este año, te dejo este regalo.$B$BEspero que te guste. ¡Feliz navidad!$B$B- Gran Padre Invierno."' WHERE `entry` = 8803;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Este bonito regalo tiene tu nombre.$B$B¿A qué esperas para abrirlo?' WHERE `entry` = 8768;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Hay una nota en el regalo que dice:$B$BPara $N:$B$B"Que tengas unas felices fiestas.$B$B- Gran Padre Invierno."' WHERE `entry` = 8744;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '"Aquí tienes un fantástico regalo del Gran Padre Invierno y de tus amigos de los Pastos Bosquehumeante."' WHERE `entry` = 8769;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Un regalo que tiene pinta de haber sido agitado muchas veces.$B$BTiene una nota que dice:$B$B"Para un $r $c muy especial."' WHERE `entry` = 8767;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Un regalo que tiene pinta de haber sido agitado muchas veces.$B$BTiene una nota que dice:$B$B"Para un $r $c muy especial."' WHERE `entry` = 8788;

UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'La máquina "Conservante de acebo" es una maquina que parece buena al principio con sólo verla, pero si te pones a examinarla detenidamente, te revelta que ha sido manipulada por los goblins. Aun así, tu habilidad de cocina parece que te permite entender cómo funciona.$B$BVes que hay acebo fresco en la máquina. Simplemente necesitas meter un poco de sal de roca y 5 monedas de oro para que la máquina empiece a funcionar.', `OfferRewardText_loc6` = 'La maquina hace un ruido extraño y empieza a funcionar. Ya no hay nada que pueda pararla. De repente, empieza a salir humo muy frío. ¡Mira, aparece un acebo en conserva!' WHERE `entry` = 8799;

UPDATE `locales_quest` SET `OfferRewardText_loc6` = '<Myralion toma la empuñadura y la observa con detenimiento.>$B$B¿Esto viene de la ciudadela? ¿Qué hacía ahí? Está claro que esta hoja no la ha creado la Plaga.$B$BLas armas de tal calidad solo las pueden crear unas pocas de las criaturas de Azeroth. Tendremos que preguntarles si pueden ayudarnos a identificarla.', `RequestItemsText_loc6` = '¿Qué has descubierto?' WHERE `entry` = 24554;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '<Caladis toma la empuñadura y la observa con detenimiento.>$B$B¿Esto viene de la ciudadela? ¿Qué hacía ahí? Está claro que esta hoja no la ha creado la Plaga.$B$BLas armas de tal calidad solo las pueden crear unas pocas de las criaturas de Azeroth. Tendremos que preguntarles si pueden ayudarnos a identificarla.', `RequestItemsText_loc6` = '¿Qué has descubierto?' WHERE `entry` = 14443;

UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Debemos recuperar el libro de El Pacto de Plata. Sin ese libro de Krasus no tenemos muchas posibilidades de resolver el misterio de la espada de la Corona de Hielo o de activar su poder.', `RequestItemsText_loc6` = '¿Qué información me traes?' WHERE `entry` = 24555;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'No podemos dejar que el libro caiga en manos de los Atracasol. Para que podamos identificar la espada y descubrir su poder, tendremos que encontrar una manera de recuperar el libro de Krasus.' WHERE `entry` = 14444;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '<El magister Hathorel lee las órdenes que has recuperado.>$B$BEl agente que has interceptado supuestamente iba a llevar el Escrito del Reposo del Dragón a Corona de Hielo, tras reunirse con un tal arcanista Tybalin en el Enclave de Plata. Tenemos que conseguir ese libro, $N, y solo tendremos una oportunidad antes de que abandone la ciudad. Debemos actuar con rapidez.', `RequestItemsText_loc6` = '¿Tenía el agente algo que podamos aprovechar?' WHERE `entry` = 24557;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '<El arcanista Tybalin lee las órdenes que has recuperado.>$B$BEl agente que has interceptado supuestamente iba a llevar el Escrito del Reposo del Dragón a Corona de Hielo, tras reunirse con un tal magister Hathorel en el Santuario Atracasol. Tenemos que conseguir ese libro, $N, y solo tendremos una oportunidad antes de que abandone la ciudad. Debemos actuar con rapidez.', `RequestItemsText_loc6` = '¿Tenía el agente algo que podamos aprovechar?' WHERE `entry` = 14457;

UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Sabía que Shandy encontraría una forma de ayudarnos. Deja que encante el tabardo y lo prepare para el uso. Con un poco de suerte, el dueño del tabardo nunca sabrá que nos lo llevamos y Shandy se ocupará del resto.', `RequestItemsText_loc6` = '¿Shandy te ha podido ayudar a conseguir un tabardo de los Atracasol?' WHERE `entry` = 20438;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Sabía que Shandy encontraría una forma de ayudarnos. Deja que encante el tabardo y lo prepare para el uso. Con un poco de suerte, el dueño del tabardo nunca sabrá que nos lo llevamos y Shandy se ocupará del resto.', `RequestItemsText_loc6` = '¿Shandy te ha podido ayudar a conseguir un tabardo de El Pacto de Plata?' WHERE `entry` = 24556;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Un trabajo excelente. Ahora que el escrito está en nuestras manos, tenemos que llevárselo a Myralion Resplandor lo antes posible. No podemos arriesgarnos a que el Pacto de Plata nos arrebate el libro una vez más.$B$BLe devolveré el tabardo a Shandy y me aseguraré de que se le recompense por su ayuda. Una vez que el libro esté en manos de Myralion, el Pacto de Plata dejará de ser una amenaza.', `RequestItemsText_loc6` = '¿Has recuperado el libro de Reposo del Dragón?' WHERE `entry` = 24451;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Un trabajo excelente. Ahora que el escrito está en nuestras manos, tenemos que llevárselo a Caladis Lanza Reluciente lo antes posible. No podemos arriesgarnos a que los Atracasol nos arrebaten el libro una vez más.$B$BLe devolveré el tabardo a Shandy y me aseguraré de que se le recompense por su ayuda. Una vez que el libro esté en manos de Caladis, los Atracasol dejarán de ser una amenaza.', `RequestItemsText_loc6` = '¿Has recuperado el libro de Reposo del Dragón?' WHERE `entry` = 20439;

UPDATE `locales_quest` SET `OfferRewardText_loc6` = '<Myralion recibe el pesado libro y empieza a ojearlo.>$B$BLos símbolos de la hoja me eran conocidos, y ahora sé por qué. Sin duda se trata de una espada forjada por los dragones, entregada a una de las razas mortales, ¿pero qué espada es esta y cómo llegó a Corona de Hielo?', `RequestItemsText_loc6` = 'Esperábamos tu regreso. ¿Qué te ha demorado?' WHERE `entry` = 24558;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '<Caladis recibe el pesado libro y empieza a ojearlo.>$B$BLos símbolos de la hoja me eran conocidos, y ahora sé por qué. Sin duda se trata de una espada forjada por los dragones, entregada a una de las razas mortales, ¿pero qué espada es esta y cómo llegó a Corona de Hielo?', `RequestItemsText_loc6` = 'Esperábamos tu regreso. ¿Qué te ha demorado?' WHERE `entry` = 24454;

UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Has restaurado la hoja de Quel\'Delar, ¡desafiando abiertamente las palabras de la Reina de Sangre Lana\'thel! Aún hay que templar la hoja, antes de demostrarle a la reina la insensatez de sus palabras.', `RequestItemsText_loc6` = '¿Has podido reconstruir Quel\'Delar?' WHERE `entry` = 24559;
