UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Espero que comprendas que no te puedo dejar pasar a la Fuente del Sol sin más, $N. Es el lugar más sagrado de los sin\'dorei y acabamos de recuperarlo.$B$B¿Piensas darnos esperanzas con historias de Quel\'Delar? Si no tienes pruebas de lo que dices, no veo razón para creer en tus palabras.', `RequestItemsText_loc6` = '¿Qué te trae por aquí?' WHERE `entry` = 24562;
