UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Es verdad que eres portador de Quel\'Delar. Este es un gran día para toda Quel\'Thalas y los sin\'dorei. Tienes mi permiso para entrar en la Fuente del Sol y completar la restauración de la espada. Mantén la cabeza alta, $N. Los hijos de Lunargenta han soñado con este día durante años.', `RequestItemsText_loc6` = '¿Has visitado el lugar en el que Thalorien cayó defendiendo la Fuente del Sol?' WHERE `entry` = 24563;
