 UPDATE `locales_achievement_reward` SET `subject_loc7` = 'Buscar muchisimo mas', `text_loc7` = '$gQuerido solitario:Querida solitaria;,$B$B Queremos reconocer tu tenacidad por recorrer mazmorras con gente a la que no conoces. Con un poco de suerte, les habras enseñado a los novatos cosas nuevas.$B$BSeremos breves: hemos oido que te gustan los grupos aleatorios. Y como nunca sabes que te va a tocar, te regalamos un perrito para que te haga compañia. O algo.$B$BAbrazos,$B$BTus amigos del equipo de Desarrollo de WoW' WHERE `entry` = '4478'; 
 UPDATE `locales_achievement_reward` SET `subject_loc7` = 'Conocimiento superior', `text_loc7` = 'Felicidades por completar tus estudios de Las Escuelas de la Magia Arcana. En reconocimiento a tu dedicacion, te adjunto este volumen especial para completar la serie.$B$BEn este tomo encontras de todo, entretenimiento, diversion, pero voy a dejar que lo descubras por tu $gmismo:misma;.$B$BAtentamente,$B$BRhonin' WHERE `entry` = '1956'; 
 
